<?php// PHP program to calculate Volume // and Surface area of Sphere   function volume( $r){    $pi = 3.14159;    $vol = (4 / 3) * $pi * $r * $r * $r;    return $vol; } function surface_area( $r){    $pi = 3.14159;         $sur_ar = 4 * $pi * $r * $r;    return $sur_ar;} $radius = 12;$vol; $sur_area; $vol = volume($radius);$sur_area = surface_area($radius); echo ("Volume Of Sphere : " );echo($vol);echo( " \nSurface Area Of Sphere :");echo( $sur_area); 